import React from 'react';
import { Redirect } from 'react-router-dom';
import getTrainers from '../../trainers-get';
import './TrainerInfo.scss';

export default class TrainerInfo extends React.Component {

    constructor() {
        super();
        this.state = {
            trainer: {}
        };
    }

    componentDidMount() {
        fetch('/rest/trainerprofiles')
            .then(protrainers => {
                this.setState({ protrainers })
                let trainerId = this.props.match.params.trainerId;
                // let trainer = getTrainers()
                let trainer = protrainers
                    .find((trainer) => trainer.id === trainerId);
                this.setState({ trainer });
            }).catch(error => console.log(error));
    }

    render() {
        const { trainer } = this.state;
        // let url = require(`../common/images/profile-img/${trainer.id}.jpg`);
        if (!trainer) {
            return <Redirect to='/not-found' />;
        }
         else {
            return (
                <div className='Info'>
                    <h1>{trainer.firstname} {trainer.lastname}</h1>
                    <div className='Info-content'>
                        <img
                            className='Info-content-image profile-img'
                            src={trainer.trainerImage}
                            alt={trainer.lastname}
                        />
                        <div className='Info-content-textbox'>
                            <p className='Info-content-text'>
                                {trainer.description}
                            </p>
                        </div>
                    </div>
                </div>
            );
        }
    }
}
