import React from 'react';

export default class TrainerInfo extends React.Component {

    constructor() {
        super();
        this.state = {
            trainer: {}
        };
    }

    componentDidMount() {
        let trainerId = this.props.match.param.trainerId;
        let trainer = getTrainers().find((trainer) => (
            trainer.id === trainerId
        ));
            this.setState({ trainer });
    }

    render() {
        return (
            <div>
                <h1>{this.state.trainer.firstname}</h1>
            </div>
        );
    }
}
